(200, 1, '2024-12-04 15:14:11', '2024-12-04 12:14:11', '<!-- wp:image {\"id\":198,\"width\":\"978px\",\"height\":\"auto\",\"sizeSlug\":\"full\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-full is-resized\"><img src=\"http://localhost/praktika/wp-content/uploads/2024/12/тренера.png2_.png\" alt=\"\" class=\"wp-image-198\" style=\"width:978px;height:auto\"/></figure>\n<!-- /wp:image -->', 'Тренера', '', 'inherit', 'closed', 'closed', '', '16-revision-v1', '', '', '2024-12-04 15:14:11', '2024-12-04 12:14:11', '', 16, 'http://localhost/praktika/?p=200', 0, 'revision', '', 0),
(202, 1, '2024-12-04 15:16:44', '2024-12-04 12:16:44', '<!-- wp:image {\"id\":198,\"width\":\"978px\",\"height\":\"auto\",\"sizeSlug\":\"full\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-full is-resized\"><img src=\"http://localhost/praktika/wp-content/uploads/2024/12/тренера.png2_.png\" alt=\"\" class=\"wp-image-198\" style=\"width:978px;height:auto\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->', 'Тренера', '', 'inherit', 'closed', 'closed', '', '16-revision-v1', '', '', '2024-12-04 15:16:44', '2024-12-04 12:16:44', '', 16, 'http://localhost/praktika/?p=202', 0, 'revision', '', 0);
